package JAVABASICO;

public class Variables {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// Tipos de datos en JAVA
		
		// Enteros 
		int num1 = 29; /*-2,147,483,648 hasta 2,147,483,647*/
		short num2 = 45; /*-32,768 hasta 32,767*/
		long num3 = 289566276668899L;
		byte num4 = 3;/*-128 hasta 127*/
		System.out.println(num1);
		System.out.println(num2);
		System.out.println(num3);
		System.out.println(num4);
		
		// Decimales
		float num5 = 3.5789f; /*De 6 a 7 cifras decimales*/
		double num6 = 3.567836578934; /*15 cifras decimales*/
		System.out.println(num5);
		System.out.println(num6);
		
		// Char para presentar caracteres
		char letra = 'a';
		System.out.println(letra);
		
		// Boolean 
		boolean verdad = true;
		boolean falso = false;
		System.out.println(verdad);
		System.out.println(falso);
		
		
	}
}
